mapper.update:
UPDATE item_differentiation_infos
SET code       = #{code},
    content    = #{content},
    annotation = #{annotation}
WHERE id = #{id}

mapper.select:
SELECT * FROM item_differentiation_infos WHERE id IN (SELECT item_differentiation_id FROM item_differentiations WHERE prescription_id = ( SELECT id FROM prescription_infos WHERE
name LIKE CONCAT('%', #{name}, '%')
) )
